    private void btnDesconectarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDesconectarActionPerformed
        try {
            String nodosDesconectar = txtNodosDesconectar.getText().trim();
            String[] nodos = nodosDesconectar.split(" ");
            if (nodos.length == 2) {
                int nodo1 = Integer.parseInt(nodos[0]);
                int nodo2 = Integer.parseInt(nodos[1]);
                grafo.desconectar(nodo1, nodo2);
                textAreaMostrarNodosDespuesDesconectar.setText("Conexiones en el grafo después de desconectar los nodos " + nodo1 + " y " + nodo2 + ":\n" + grafo.obtenerConexiones());
            } else {
                JOptionPane.showMessageDialog(this, "Ingrese dos nodos válidos para desconectar.");
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Ingrese números válidos para los nodos.");
        }
    }//GEN-LAST:event_btnDesconectarActionPerformed

