    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        try {
            int nodoEliminar = Integer.parseInt(txtNodoEliminar.getText().trim());
            grafo.eliminar(nodoEliminar);
            textAreaMostrarDespuesEliminarNodo.setText("Conexiones en el grafo después de eliminar el nodo " + nodoEliminar + ":\n" + grafo.obtenerConexiones());
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Ingrese un número válido para eliminar.");
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

