    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        String datosEntrada = txtDatosEntrada.getText();
        String[] nodos = datosEntrada.split(" ");
        for (String nodo : nodos) {
            try {
                int valor = Integer.parseInt(nodo.trim());
                grafo.insertar(valor);
            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(this, "Ingrese solo números separados por espacios.");
            }
        }

        String conexiones = txtConexionesNodos.getText();
        String[] conexionesLineas = conexiones.split("\n");
        for (String linea : conexionesLineas) {
            if (linea.trim().equalsIgnoreCase("fin")) {
                break;
            }
            String[] nodosConectados = linea.split(" ");
            if (nodosConectados.length == 2) {
                try {
                    int nodo1 = Integer.parseInt(nodosConectados[0].trim());
                    int nodo2 = Integer.parseInt(nodosConectados[1].trim());
                    grafo.conectar(nodo1, nodo2);
                } catch (NumberFormatException e) {
                    JOptionPane.showMessageDialog(this, "Ingrese conexiones válidas en el formato 'nodo1 nodo2'.");
                }
            }
        }

        txtAreaMostrarNodos.setText("Conexiones en el grafo:\n" + grafo.obtenerConexiones());
    }//GEN-LAST:event_btnEnviarActionPerformed


